import React from 'react'

import classes from './style.module.css'

export const Card = ({cards, onRemove, addCard, addMarker}) => (
    cards.map(card => {
         return (
            <div className = {classes.card} key = {card.id}>
                <h3 className = {classes.text}>{card.text}</h3>
                <div className = {card.col ? classes.green : classes.red}></div>
                <button className = {classes.draft} 
                    onClick = {() => {
                        onRemove(card.id);
                        addCard(card.text, 'draft')
                    }}>draft</button>
                <button className = {classes.mark}
                    onClick = {() => addMarker(card) }
                >mark</button>
            </div>
        )
    })
)