import {
    SHOW_DRAFT, ADD_DRAFT, FETCH_DRAFT, 
    REMOVE_DRAFT, ADD_CARDS, FETCH_CARDS,
    REMOVE_CARD, ADD_MARKER, DELETE_MARKER
} from './actionTypes'

let handlers = { 

    // Тут меняем состояние драфта
    [SHOW_DRAFT] : (state) => ({...state, loading: true}),
    [ADD_DRAFT] : (state, {payload}) => ({
        ...state, 
        notes: [...state.notes, payload]
    }),
    [FETCH_DRAFT] : (state, {payload}) => ({...state, notes: payload, loading: false}),
    [REMOVE_DRAFT] : (state, {payload}) => ({
        ...state, 
        notes: state.notes.filter(note => note.id !== payload)
    }),

    // Тут меняем состояние сохраненных карт

    [ADD_CARDS] : (state, {card}) => ({
        ...state, 
        cards: [...state.cards, card]
    }),
    [FETCH_CARDS] : (state, {payload}) => ({...state, cards: payload, loading: false}),
    [REMOVE_CARD] : (state, {payload}) => ({
        ...state, 
        cards: state.cards.filter(card => card.id !== payload)
    }),

    // Тут меняем состояние маркера

    [ADD_MARKER] : (state) => {
        console.log(state.count)
       return ({
            ...state,
            count: ++state.count
        })
    },
    [DELETE_MARKER] : (state) => ({
        ...state,
        count: --state.count 
    })


    
}

// Наш редьюсер:

export const reducer = (state, action) => {
    const handle = handlers[action.type]  || handlers.DEFAULT;
    return handle(state, action);
}